"use strict";

var config = require('./config');
var express = require('express');
var morgan = require('morgan');
var cors = require('cors');
var manga = require('./modulos/manga/ruta');
var usuario = require('./modulos/usuario/ruta');
var auth = require('./modulos/auth/ruta');

//express
var app = express();

//Middleware
app.use(cors());
app.use(morgan('dev'));
// fixing "413 Request Entity Too Large" errors
app.use(express.json({
  limit: "50mb",
  extended: true
}));
app.use(express.urlencoded({
  limit: "50mb",
  parameterLimit: 1000000,
  extended: true
}));

//configuration
app.set('port', config.app.port);

//routes

app.use('/api/manga', manga);
app.use('/api/usuario', usuario);
app.use('/api/auth', auth);
module.exports = app;
//# sourceMappingURL=data:application/json;charset=utf-8;base64,

sourcesContent:
const config = require('./config');
const express = require ('express');
const morgan = require ('morgan');
const cors = require('cors');
const manga = require('./modulos/manga/ruta');
const usuario = require('./modulos/usuario/ruta');
const auth = require('./modulos/auth/ruta');


//express
const app = express();

//Middleware
app.use(cors());
app.use(morgan('dev'));
// fixing "413 Request Entity Too Large" errors
app.use(express.json({limit: "50mb", extended: true}))
app.use(express.urlencoded({limit: "50mb",parameterLimit: 1000000 ,extended: true}))

//configuration
app.set('port', config.app.port)

//routes

app.use('/api/manga',manga);
app.use('/api/usuario',usuario);
app.use('/api/auth',auth);

module.exports= app

